8mb4_unicode_ci;   CREATE TABLE     OrderItems (         id INT UNSIGNED AUTO_INCREMENT PRIMARY KEY,         qty_total INT UNSIGNED NOT NULL,         amount_total DECIMAL(12, 2) NOT NULL DEFAULT 0.00,         created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,         updated_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,         transaction_id INT UNSIGNED NOT NULL,         product_id INT UNSIGNED NOT NULL,         CONSTRAINT fk_orderitems_transaction FOREIGN KEY (transaction_id) REFERENCES Transactions (id) ON DELETE CASCADE ON UPDATE CASCADE,         CONSTRAINT fk_orderitems_product FOREIGN KEY (product_id) REFERENCES Products (id) ON DELETE CASCADE ON UPDATE CASCADE     ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COLLATE = utf8mb4_unicode_ci;   INSERT INTO Roles (name, description) VALUES ('admin', 'Quyền quản trị hệ thống, có thể thêm, sửa, xóa dữ liệu và quản lý user'), ('user', 'Quyền người dùng bình thường, chỉ có thể xem và thao tác hạn chế trong hệ thống');   INSERT INTO Admins (username, email, full_name, password_hash, level, description, role_id) VALUES ('superadmin', 'superadmin@example.com', 'Nguyen Van A', '$2y$10$examplehash1', 3, 'Quản trị viên cao cấp, toàn quyền trên hệ thống', 1), ('admin1', 'admin1@example.com', 'Tran Thi B', '$2y$10$examplehash2', 2, 'Quản lý nội dung và người dùng', 1), ('admin2', 'admin2@example.com', 'Le Van C', '$2y$10$examplehash3', 1, 'Hỗ trợ quản lý vận hành', 1), ('useradmin', 'useradmin@example.com', 'Pham Thi D', '$2y$10$examplehash4', 0, 'Admin cấp thấp, quyền hạn giới hạn', 1);   INSERT INTO PostTypes (name, slug, description) VALUES ('Mẹo hay tiêu dùng', 'meo-hay-tieu-dung', 'Tổng hợp các mẹo vặt hữu ích trong đời sống hàng ngày'), ('Bài viết nổi bật', 'bai-viet-noi-bat', 'Những bài viết được đánh giá nổi bật, quan tâm nhiều nhất'), ('Đặc sản vùng miền', 'dac-san-vung-mien', 'Giới thiệu các đặc sản nổi tiếng từ các vùng miền Việt Nam'), ('Công thức nấu ăn', 'cong-thuc-nau-an', 'Các